package Dayone;

public class simplecalculator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
double num1 = 10.5;
double num2 = 2.5;
System.out.println(num1 + " + " + num2 + " = " + (num1 + num2));
System.out.println(num1 + " - " + num2 + " = " + (num1 - num2));
System.out.println(num1 +  " * " + num2 + " = " + (num1 * num2));
System.out.println(num1 + " / " + num2 + " = " + (num1 / num2));
System.out.println(num1 + " % " + num2 + " = " + (num1 % num2));


	}

}
